import React from "react";
import { Link, useParams } from "react-router-dom";
import { alignProperty } from "@mui/material/styles/cssUtils";
import { Button, Card, CardContent, Grid, Typography } from "@mui/material";

interface Module {
  id: number;
  title: string;
  duration: string;
}

interface CourseDetails {
  title: string;
  price: string;
  description: string;
  duration: string;
  modules: Module[];
  frequentlyBoughtTogether: number[];
}

const CourseDetailPage: React.FC = () => {
  const { id } = useParams<{ id?: string }>();

  if (!id) {
    return <div>Invalid video ID</div>;
  }

  const courseDetails: Record<string, CourseDetails> = {
    // Your course details here...

    "1": {
      title: "Introduction to React",
      price: "$20.00",
      description: "Course description goes here.",
      duration: "2 hours",
      modules: [
        { id: 1, title: "Module 1", duration: "30 mins" },
        { id: 2, title: "Module 2", duration: "45 mins" },
        { id: 3, title: "Module 3", duration: "50 mins" },
        { id: 4, title: "Module 4", duration: "35 mins" },
        { id: 5, title: "Module 5", duration: "55 mins" },
      ],
      frequentlyBoughtTogether: [2, 3, 4],
    },

    "2": {
      title: "JavaScript Fundamentals",
      price: "$20.00",
      description: "Course description goes here.",
      duration: "2 hours",
      modules: [
        { id: 1, title: "Module 1", duration: "30 mins" },
        { id: 2, title: "Module 2", duration: "45 mins" },
        { id: 3, title: "Module 3", duration: "50 mins" },
        { id: 4, title: "Module 4", duration: "35 mins" },
        { id: 5, title: "Module 5", duration: "55 mins" },
      ],
      frequentlyBoughtTogether: [2, 3, 4],
    },
    "3": {
      title: "CSS Styling Techniques",
      price: "$20.00",
      description: "Course description goes here.",
      duration: "2 hours",
      modules: [
        { id: 1, title: "Module 1", duration: "30 mins" },
        { id: 2, title: "Module 2", duration: "45 mins" },
        { id: 3, title: "Module 3", duration: "50 mins" },
        { id: 4, title: "Module 4", duration: "35 mins" },
        { id: 5, title: "Module 5", duration: "55 mins" },
      ],
      frequentlyBoughtTogether: [2, 3, 4],
    },
    "4": {
      title: "Responsive Web Design",
      price: "$20.00",
      description: "Course description goes here.",
      duration: "2 hours",
      modules: [
        { id: 1, title: "Module 1", duration: "30 mins" },
        { id: 2, title: "Module 2", duration: "45 mins" },
        { id: 3, title: "Module 3", duration: "50 mins" },
        { id: 4, title: "Module 4", duration: "35 mins" },
        { id: 5, title: "Module 5", duration: "55 mins" },
      ],
      frequentlyBoughtTogether: [2, 3, 4],
    },
    "5": {
      title: "State Management in React",
      price: "$20.00",
      description: "Course description goes here.",
      duration: "2 hours",
      modules: [
        { id: 1, title: "Module 1", duration: "30 mins" },
        { id: 2, title: "Module 2", duration: "45 mins" },
        { id: 3, title: "Module 3", duration: "50 mins" },
        { id: 4, title: "Module 4", duration: "35 mins" },
        { id: 5, title: "Module 5", duration: "55 mins" },
      ],
      frequentlyBoughtTogether: [2, 3, 4],
    },
    "6": {
      title: "Building RESTful APIs",
      price: "$20.00",
      description: "Course description goes here.",
      duration: "2 hours",
      modules: [
        { id: 1, title: "Module 1", duration: "30 mins" },
        { id: 2, title: "Module 2", duration: "45 mins" },
        { id: 3, title: "Module 3", duration: "50 mins" },
        { id: 4, title: "Module 4", duration: "35 mins" },
        { id: 5, title: "Module 5", duration: "55 mins" },
      ],
      frequentlyBoughtTogether: [2, 3, 4],
    },
  };

  const videoDetails = courseDetails[id];

  if (!videoDetails) {
    return <div>Video not found</div>;
  }
  const handleAddToCart = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const {
    title,
    price,
    description,
    duration,
    modules,
    frequentlyBoughtTogether,
  } = videoDetails;

  const videoContainerStyle: React.CSSProperties = {
    background: "rgba(175, 219, 245, 0.5)",
    padding: "20px",
    borderRadius: "10px",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    margin: "0 120px", // Add margin to the left and right
  };
  const frevideoContainerStyle: React.CSSProperties = {
    padding: "20px",
    borderRadius: "10px",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    margin: "0 120px",
  };

  const titleContainerStyle: React.CSSProperties = {
    flex: 1,
    marginRight: "40px",
  };

  const orderButtonStyle: React.CSSProperties = {
    borderRadius: "5px",
    background: "#3498db",
    color: "white",
    padding: "12px",
    cursor: "pointer",
  };

  const formContainerStyle: React.CSSProperties = {
    background: "rgba(175, 219, 245, 0.2)",
    padding: "20px",
    borderRadius: "10px",
    margin: "20px 350px",
    color: "#3498db",
  };
  const textContainerStyle: React.CSSProperties = {
    maxWidth: "1100px",
    margin: "auto",
    fontSize: "15px",
    color: "#3498db",
  };
  const moduleContainerStyle: React.CSSProperties = {
    background: "white",
    color: "#3498db",
    borderRadius: "10px",
    padding: "10px",
    marginBottom: "10px",
  };
  const videoStyle: React.CSSProperties = {
    width: "300px",
    margin: "20px",
    padding: "20px",
    height: "100px",
    background: "rgba(175, 219, 245, 0.5)",
    borderRadius: "10px",
